import React, { useContext,useState } from "react";
import { useCart } from "../../hooks/cart/useCart";
import Pagination from '../Pagination';

export default function CartPage() {

  const [setCart] = useState([])
  const [loading, setLoading] = useState(false)
  const [currentPage, setCurrentPage] = useState(1)
  const [newsPerPage] = useState(5)

  const { cart, removeFromCart,PlusFromCart } = useCart();

  const lastNewsIndex = currentPage * newsPerPage
  const firstNewsIndex = lastNewsIndex - newsPerPage
  const currentNews = cart.slice(firstNewsIndex, lastNewsIndex)

  const paginate = pageNumber => setCurrentPage(pageNumber)

  const nextPage = () => setCurrentPage(prev => prev + 1)
  const prevPage = () => setCurrentPage(prev => prev - 1)

  return (
    <div>
      <table className="table">
        <tbody>
          {cart?.map((product) => (
            <tr>
              <td>{product.title}</td>

              <td>
              <button
                  className="btn btn-success"
                  onClick={() => removeFromCart(product)}
                >
                  Remove
                </button>

                <button
                  className="btn btn-success"
                  onClick={() => PlusFromCart(product)}
                >
                  Not Remove
                </button>
              </td>


            </tr>

          ))}
        </tbody>
        <Pagination
                    newsPerPage={newsPerPage}
                    totalNews={cart.length}
                    paginate={paginate}

                />
                <button className='btnBookmarks' onClick={prevPage}>Prev</button>
                <button className='btnBookmarks ms-2' onClick={nextPage}>Next</button>
      </table>

    </div>

  );
}
