import React, { useContext,useState,useEffect,useMemo } from "react";
import { useCart, } from "../../hooks/cart/useCart";

import { Link } from "react-router-dom";
import Inform from "../Main/Inform";
import Pagination from "../../Pagination";
import CartContextProvider from "./CartContextProvider";
export default function CartPage() {


let PageSize = 10;
    const [news, SetNews] = useState([])
    const [input, setInput] = useState('');
    let [limit, setLimit] = useState(50);
    let [publishedAt, setPublishedAt] = useState("publishedAt");
    let [btn, setBtn] = useState("main-btn");
    let [loader, setLoader] = useState("hidden");

  const { cart } = useCart();





    useEffect(() => {
        const getBookmarks = async () => {
            setLoading(true)
            const bookmarks = JSON.parse(localStorage.getItem('bookmarks'))
            setArticles(bookmarks)
            setLoading(false)
        }
        getBookmarks()
    }, [])


    const [currentPage, setCurrentPage] = useState(1);

  const currentTableData = useMemo(() => {
    const firstPageIndex = (currentPage - 1) * PageSize;
    const lastPageIndex = firstPageIndex + PageSize;
    return cart.slice(firstPageIndex, lastPageIndex);
  }, [currentPage]);


  let getNews = () => {
    async function getData() {


      SetNews(cart);

      setPosts(cart.map((item) => <Inform key={item.id} data={item} />));
      setLoader("hidden");
      setBtn("main-btn");
    }
    getData();
  };







  useEffect(getNews, [limit]);

  return (

    <div>
      <table className="table">
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>URL</th>
            <th>TITLE</th>
            <th>updatedAt</th>
            <th>publishedAt</th>
          </tr>
        </thead>
        <tbody>
          {currentTableData.map(item => {
            return (
              <tr>
                <td>{item.id}</td>
                <td>{item.url}</td>
                <td>{item.title}</td>
                <td>{item.updatedAt}</td>
                <td>{item.publishedAt}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
      <Pagination
        className="arrow left"
        totalCount={cart.length}
        pageSize={PageSize}
        onPageChange={page => setCurrentPage(page)}
      />
      </table>
    </div>



  );
}
