import React, { useContext,useState,useEffect,useMemo } from "react";
import { useCart, } from "../../hooks/cart/useCart";

import { Link } from "react-router-dom";
import Inform from "../Main/Inform";
import Pagination from "react-js-pagination";
import CartContextProvider from "./CartContextProvider";
export default function CartPage() {



  const [newsPerPage] = useState(3)
  let [posts, setPosts] = useState(
    );
    const [news, SetNews] = useState([])
    const [input, setInput] = useState('');
    let [limit, setLimit] = useState(50);
    let [publishedAt, setPublishedAt] = useState("publishedAt");
    let [btn, setBtn] = useState("main-btn");
    let [loader, setLoader] = useState("hidden");

  const { cart } = useCart();

  const [articles, setArticles] = useState([])
    const [loading, setLoading] = useState(false)


    useEffect(() => {
        const getBookmarks = async () => {
            setLoading(true)
            const bookmarks = JSON.parse(localStorage.getItem('bookmarks'))
            setArticles(bookmarks)
            setLoading(false)
        }
        getBookmarks()
    }, [])


    const [currentPage, setCurrentPage] = useState(1);

  const currentTableData = useMemo(() => {
    const firstPageIndex = (currentPage - 1) * newsPerPage;
    const lastPageIndex = firstPageIndex + newsPerPage;
    return cart.slice(firstPageIndex, lastPageIndex);
  }, [currentPage]);


  let getNews = () => {
    async function getData() {


      SetNews(cart);

      setPosts(cart.map((item) => <Inform key={item.id} data={item} />));
      setLoader("hidden");
      setBtn("main-btn");
    }
    getData();
  };
  const [pageState, setPageState] = useState({
    activePage: 1
  });

  function handlePageChange(pageNumber) {
      setPosts(cart.map((item) => <Inform key={item.id} data={item} />));
      setLoader("loader");
    setPageState({ activePage: pageNumber});
  }

  const showMore = () => {

    setLoader("loader");
    setLimit((prevLimit) => prevLimit + 1);


  };
  const body = {
    background: "#002137"
  }



  useEffect(getNews, [limit]);

  return (

    <div>
      <table className="table">
        <tbody>         
        <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>FIRST NAME</th>
            <th>LAST NAME</th>
            <th>EMAIL</th>
            <th>PHONE</th>
          </tr>
        </thead>
        <tbody>
          {currentTableData.map(item => {
            return (
              <tr>
                <td>{item.id}</td>
                <td>{item.title}</td>
                <td>{item.url}</td>
                <td>{item.publishedAt}</td>
                <td>{item.updatedAt}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
        </tbody>

<center></center>
      </table>
      <div className='displayBookmarks'>


      <Pagination
        className="pagination-bar"
        currentPage={currentPage}
        totalCount={cart.length}
        pageSize={newsPerPage}
        onPageChange={page => setCurrentPage(page)}
      />
    </div>

    </div>

  );
}
