import React, { useContext,useState,useEffect } from "react";
import { useCart } from "../../hooks/cart/useCart";
import Pagination from '../Pagination';
import { Link } from "react-router-dom";
import Inform from "../Main/Inform";
export default function CartPage() {


  const [currentPage, setCurrentPage] = useState(1)
  const [newsPerPage] = useState(3)
  let [posts, setPosts] = useState(
    );
    const [news, SetNews] = useState([])
    const [input, setInput] = useState('');
    let [limit, setLimit] = useState(50);
    let [publishedAt, setPublishedAt] = useState("publishedAt");
    let [btn, setBtn] = useState("main-btn");
    let [loader, setLoader] = useState("hidden");

  const { cart } = useCart();

  let getNews = () => {
    async function getData() {
      const response = await fetch(
        `https://api.spaceflightnewsapi.net/v3/articles?_limit=${limit}`
      );
      SetNews(response.data);
      const data = await response.json();
      setPosts(data.map((item) => <Inform key={item.id} data={item} />));
      setLoader("hidden");
      setBtn("main-btn");
    }
    getData();
  };

  const showMore = () => {

    setLoader("loader");
    setLimit((prevLimit) => prevLimit + 9);


  };
  const body = {
    background: "#002137"
  }

  const showMore1 = () => {

    setLoader("loader");
    setLimit((prevLimit) => prevLimit - 9);


  };

  const lastNewsIndex = currentPage * newsPerPage
  const firstNewsIndex = lastNewsIndex - newsPerPage
  const currentNews = cart.slice(firstNewsIndex, lastNewsIndex)

  const paginate = pageNumber => setCurrentPage(pageNumber)
  useEffect(getNews, [limit]);

  return (
    <div>
      <table className="table">
        <tbody>
        <useCart articles={currentNews} />
          {cart?.map((product) => (
            <tr>
              {posts}
              <td>{product.title}</td>
              <td><td> <Link to={`/news/${product.id}`} className='card-url'>
          Читать новость
          </Link></td>
              </td>
            </tr>

          ))}
        </tbody>

<center></center>

        <Pagination
                    newsPerPage={newsPerPage}
                    totalNews={cart.length}
                    paginate={paginate}
                />
      </table>

    </div>

  );
}
