    background: "#002137"
  }

  const showMore1 = () => {

    setLoader("loader");
    setLimit((prevLimit) => prevLimit - 1);


  };

  useEffect(getNews, [limit]);

  return (

    <div>
      <table className="table">
        <tbody>         
              <td>{posts}</td>
        </tbody>

<center></center>
      </table>
      <button className={btn} onClick={showMore}>
            Page BackWard
          </button>
          <button className={btn} onClick={showMore1}>
            Page Forward
          </button>
          <p>React pagination test</p>

        <Pagination

          itemsCountPerPage={10}
          totalItemsCount={450}
          pageRangeDisplayed={1}

        />
      </div>


  );
}
