    let [limit, setLimit] = useState(1);
    let [publishedAt, setPublishedAt] = useState("publishedAt");
    let [btn, setBtn] = useState("main-btn");
    let [loader, setLoader] = useState("hidden");

  const { cart } = useCart();

  let getNews = () => {
    async function getData() {


      SetNews(cart);

      setPosts(cart.map((item) => <Inform key={item.id} data={item} />));
      setLoader("hidden");
      setBtn("main-btn");
    }
    getData();
  };
  const showMore = () => {

    setLoader("loader");
    setLimit((prev) => prev + 1);


  };
  const body = {
    background: "#002137"
  }

  const showMore1 = () => {

    setLoader("loader");
    setLimit((prev) => prev - 1);


  };
  const lastNewsIndex = currentPage * newsPerPage
  const firstNewsIndex = lastNewsIndex - newsPerPage
  const currentNews = cart.slice(firstNewsIndex, lastNewsIndex)


  const paginate = pageNumber => setLimit(pageNumber)
  useEffect(getNews, );

  return (
    <div>
      <table className="table">
        <tbody>         
              <td>{posts}</td>
              <Pagination
              paginate={paginate}
                    newsPerPage={newsPerPage}
                    totalNews={cart.length}


                />
        </tbody>

<center></center>
      </table>
      <button className={btn} onClick={showMore}>
            Page BackWard
          </button>
          <button className={btn} onClick={showMore1}>
            Page Forward
          </button>

    </div>

  );
}
