import { useContext } from "react";
import { CartContext } from "../../components/Cart/CartContextProvider";

export const useCart = () => {
  const { cart, addToCart, removeFromCart } = useContext(CartContext);
  let bookmarks = localStorage.getItem('bookmarks')

  if(bookmarks) {
      bookmarks = JSON.parse(bookmarks);
      if (!bookmarks.find(item => item.id === cart.id)) {
          localStorage.setItem('bookmarks', JSON.stringify([...bookmarks, cart]));
      }
  } else {
      localStorage.setItem('bookmarks', JSON.stringify([cart]));


  }
  return {
    cart,
    addToCart,
    removeFromCart,
  };
};
