import React, { useContext,useState,useEffect } from "react";
import { useCart } from "../../hooks/cart/useCart";

export default function CartPage() {
  const { cart, removeFromCart } = useCart();
  let [limit, setLimit] = useState(50);
  let [publishedAt, setPublishedAt] = useState("publishedAt");
  let [btn, setBtn] = useState("main-btn");
  let [loader, setLoader] = useState("hidden");
  const [currentPage, setCurrentPage] = useState(1)
  const [newsPerPage] = useState(5)
  let [posts, setPosts] = useState(
    );

  useEffect(() => {
    const getBookmarks = async () => {

        const bookmarks = JSON.parse(localStorage.getItem('bookmarks'))
        setPosts(bookmarks)

    }
    getBookmarks()
}, [])


const lastNewsIndex = currentPage * newsPerPage
const firstNewsIndex = lastNewsIndex - newsPerPage
const currentNews = cart.slice(firstNewsIndex, lastNewsIndex)

const paginate = pageNumber => setCurrentPage(pageNumber)

const nextPage = () => setCurrentPage(prev => prev + 1)
const prevPage = () => setCurrentPage(prev => prev - 1)


  return (
    <div>
      <table className="table">
        <tbody>
          {cart.map((product) => (
            <tr>
              <td>{product.title}</td>
              <td>
                <button
                  className="btn btn-success"
                  onClick={() => removeFromCart(product)}
                >
                  Remove
                </button>
              </td>

            </tr>

          ))}
          <button className={btn} onClick={nextPage}>
            Page BackWard
          </button>
          <button className={btn} onClick={prevPage}>
            Page Forward
          </button>
        </tbody>
      </table>

    </div>
  );
}
