      setPosts(cart.map((item) => <Inform key={item.id} data={item} />));
      setLoader("hidden");
      setBtn("main-btn");
    }
    getData();
  };
  const [pageState, setPageState] = useState({
    activePage: 1
  });

  function handlePageChange(pageNumber) {

      setPosts(cart.map((item) => <Inform key={item.id} data={item} />));
      setLoader("loader");
    setPageState({ activePage: pageNumber});
  }

  const showMore = () => {

    setLoader("loader");
    setLimit((prevLimit) => prevLimit + 1);


  };
  const body = {
    background: "#002137"
  }



  useEffect(getNews, [limit]);

  return (

    <div>
      <table className="table">
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>FIRST NAME</th>
            <th>LAST NAME</th>
            <th>EMAIL</th>
            <th>PHONE</th>
          </tr>
        </thead>
        <tbody>
          {currentTableData.map(item => {
            return (
              <tr>
                <td>{item.id}</td>
                <td>{item.url}</td>
                <td>{item.title}</td>
                <td>{item.updatedAt}</td>
                <td>{item.publishedAt}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
